INSERT INTO `guides` VALUES (175, 1, '2', '1', 'auto_watering', 'השקיה אוטומתי בזול לגינה', 'auto Watering', 'מדריך ליצירת השקיה אוטומתי לגינה בעלות של 50 שח', '', 0, '', '[[""]]', '["שלב ראשון לחבר את הברז האלקטרוני למקור המים &#44; יתכן ותצתרכו מתאמים או&#47;וגם מפצלים אם מקור המים כבר בשימוש &#44; הסבר מפורט של הבא ","ניתן לראות בסימון כחול מספר 1 זה הברז האלקטרוני &#44; מספרים 2 3 ו-4 באדום אילו המתאמים המתחברים מהמפצל - מקור המים אל צינור ההשקיה שיצא לגינה &#44; בירוק מספר 5 זה המפצל שפיצל את מקור המים שיהיו גם עבור מכונת הכביסה &#40;צינור אפור &#41; וגם להשקיה של הגינה שכעת אנו יוצרים . למטה רואים את הצינור החום שיוצא ממתאם מספר 4 המיועד להשקיית הגינה.","אחרי שיחברנו את הברז האלקטרוני &#44; ניקח את השנאי שיש ברשותינו &#40;למי שאין ודאי רכש &#41; את החלק בקצה נחתוך &#40;מסומן באדום &#41;ונקבל שני חוטי חשמל &#44; חיובי ושלילי - לא משנה מה זה מה &#44; יש לחבר את החוטים אחד לעיגול הכחול הראשון והשני לעיגול השני &#44; מומלץ להלחים את החוטים ולסגור עם שרינק &#44; מי שאין ולא יודע יכול להשתמש בדבק חשמלאים - באיזולירבנד &#40;חשוב שלא יגיעו מים כי חשמל ומים זה מסוכן ובאחריותכם &#41;","כעת את השעון חשמל נחבר לשקע ונכוון ע&#39;פ הצורך &#44; באיזה ימים להשקות &#44;כמה זמן השקיה וכו .. את השנאי לחבר לשעון החשמל.","את צינור המים אני העברתי דרך הקיר &#44; כל אחד שיעשה לרוחו &#44; ופיצלתי אותו לכמה אזורי השקיה .","חיברתי טפטפות בכל המקומות ששתלתי דברים &#40; ישנם סוגי טפטפות שונות &#44; צריך להתאים עפ הצמח &#44;  הטפטפות השונות נותנות כמויות שונות של מים לשעה &#41; ","מקווה שעזרתי למישהו &#44; בהצלחה ... "]', '["images/guides/auto_watering/auto_watering_0.jpg","images/guides/auto_watering/auto_watering_1.jpg","images/guides/auto_watering/auto_watering_2.jpg","images/guides/auto_watering/auto_watering_3.jpg","images/guides/auto_watering/auto_watering_4.jpg","images/guides/auto_watering/auto_watering_5.jpg","images/guides/auto_watering/auto_watering_6.jpg","images/guides/auto_watering/auto_watering_7.jpg"]', '[""]', '', '["textarea","text_and_img","text_and_img","text_and_img",