INSERT INTO `guides` VALUES (167, 1, '6', '1', 'install_kodi_17_with_abeksis', 'התקנת קודי 17 למחשב פלוס התקנת אבקסיס', 'install kodi 17 with abeksis', 'התקנת קודי גירסא 17 פלוס אבקסיס - הסברים צעד אחר צעד, אבקסיס מתקין את כל מה שצריך , שפה כתוביות ערוצים live ועוד .. ', 'אבקסיס, קודי, קודי 17 , התקנת קודי, התקנת אבקסיס, התקנת סטרימר, מדריך התקנת אבקסיס, מדריך התקנת קודי', 1, 'http://justdo.co.il/display-guide.php?guide=138', '[[""]]', '["נפעיל את הקובץ שירד&#44; בהודעה שתקפוץ לחצו yes","לחצו Next","לחצו I Agree","לחצו Next","לחצו install","תסמנו את הריבוע ב v ואז לחצו על finish","כשקודי יעלה תקפוץ הודעת אבטחה של וינדוס יש לאשר אותה Allow access","נבחר באיקון של הגלגל שיניים בצד שמאל למעלה","כעת נבחר ב File manager","נלחץ על Add source","בחלון שנפתח נלחץ על <None>","כעת נזין את הכתובת http:&#47;&#47;repo.abeksis.com ונלחץ על ok","נלחץ על הרובריקה ->המלבן למטה  ","כעת נזין את השם abeksis ונלחץ ok","נחזור למסך הראשי חזרה אחרוה ניתן על ידי Backspace במקלדת &#44; ונבחר Add-ons","כעת נבחר בארגז בצד שמאל למעלה ","נלחץ על Install from zip file","בחלון שיקפוץ בנחר ב settings","כעת נבחר ב Unknown sources ","בחלון שיקפוץ נבחר ב Yes","וכעת נחזור אחורה עי כפתור  Backspace במקלדת ונלחץ שוב על Install from zip file ","בחלון שיפתח נבחר ב abeksis","וכעת נבחר ב repository.abeksis.zip","כעת נבחר ב Install from repository","נבחר Abeksis repsitory","כעת נבחר ב Program add-ons","כעת יש לבחור ב Abeksis wizard","ואז כמובן נלחץ על Install","נחזור ארבעה שלבים אחורה עד שנגיע למסך של Add-ons ושם נעמוד על Program add-ons","ואז נקליק על האופציה בצד ימין Abeksis wizard","בחלון שנפתח נקליק על Wizard for kodi 17 General","ברגע שתסתים ההתקנה יקפוץ חלון שיבקש לכבות את הקודי שההגדרות החדשות יכנסו לתוקף וכמובן נאשר זאת עי לחיצה על ok","נפעיל מחדש את kodi ומזל טוב קיבלתם קודי מוכן לפעולה ",""]', '["images/guides/install_kodi_17_with_abeksis/install_kodi_17_with_abeksis_0.jpg","images/guides/install_kodi_17_with_abeksis/install_kodi_17_with_abeksis_1.png","images/guides/install_kodi_17_with_abeksis/inst