INSERT INTO `guides` VALUES (169, 1, '7', '1', 'car_keyboard', 'תיקון מקשים של קודן', 'car keyboard', 'תיקון מקשי סיליקון של קודן ברכב ', 'תיקון קודן לרכב, תקן בעצמך קודן, תיקון קודן, קודן לרכב, עשה זאת בעצמך, DIY, מדריך לתיקון קודן, מדריך לתיקון קודן לרכב', 0, '', '[["23,39"]]', '["נפתח בזהירות את הקודן ונשלוף את המקשים ","ברגע שנהפוך את המקשים נוכל לראות נקודות שחורות","ניגזור רצועות קטנות של נייר כסף&#44; ניתן לראות בעיגול האדום ","ניקח עם קיסם או עם מברג קטן קצת דבק &#44; אני ממליץ דבק סיליקון אבל אפשר גם דבק מגע &#44; ונתחיל למרוח על העיגולים השחורים של הכפתור הבעיתי ובניהם . מומלץ שכבה דקה אבל לא לפספס אף נקודה","כעת ניקח עם הפינצטה את הרצועה של הנייר כסף ונדביק בעדינות ","כך צריכה ליראות התוצאה הסופית אחרי ההדבקה &#44; ולחזור על הפעולה על כל אחד מהכפתורים הבעיתיים ","ניתן לדבק להתיבש עפ הצורך .. ממליץ לפחות שעה &#44; ואז להחזיר את המקשים לקודן . מקווה שחסכתי לכם ...."]', '["images/guides/car_keyboard/car_keyboard_0.jpg","images/guides/car_keyboard/car_keyboard_1.jpg","images/guides/car_keyboard/car_keyboard_2.jpg","images/guides/car_keyboard/car_keyboard_3.jpg","images/guides/car_keyboard/car_keyboard_4.jpg","images/guides/car_keyboard/car_keyboard_5.jpg","images/guides/car_keyboard/car_keyboard_6.jpg","images/guides/car_keyboard/car_keyboard_7.jpg"]', '[""]', '', '["textarea","text_and_img","text_and_img","text_and_img","text_and_img","text_and_img","text_and_img","text_and_img"]');
