INSERT INTO `guides` VALUES (174, 1, '8', '1', 'cool_bottle_', 'דברים לעשות עם בקבוק', 'cool bottle ', 'רעיונות גאוניים לעשות עם בקבוקים ', '', 0, '', '[["5,21"]]', '[]', '["images/guides/cool_bottle_/cool_bottle__0.jpg"]', '["mWjfUDn8Bqg?autoplay=0&loop=0&controls=1&rel=0"]', 'W10=', '["youtube"]');
INSERT INTO `guides` VALUES (175, 1, '2', '1', 'auto_watering', 'השקיה אוטומתי בזול לגינה', 'auto Watering', 'מדריך ליצירת השקיה אוטומתי לגינה בעלות של 50 שח', '', 0, '', '[[""]]', '["שלב ראשון לחבר את הברז האלקטרוני למקור המים &#44; יתכן ותצתרכו מתאמים או&#47;וגם מפצלים אם מקור המים כבר בשימוש &#44; הסבר מפורט של הבא ","ניתן לראות בסימון כחול מספר 1 זה הברז האלקטרוני &#44; מספרים 2 3 ו-4 באדום אילו המתאמים המתחברים מהמפצל - מקור המים אל צינור ההשקיה שיצא לגינה &#44; בירוק מספר 5 זה המפצל שפיצל את מקור המים שיהיו גם עבור מכונת הכביסה &#40;צינור אפור &#41; וגם להשקיה של הגינה שכעת אנו יוצרים . למטה רואים את הצינור החום שיוצא ממתאם מספר 4 המיועד להשקיית הגינה.","אחרי שיחברנו את הברז האלקטרוני &#44; ניקח את השנאי שיש ברשותינו &#40;למי שאין ודאי רכש &#41; את החלק בקצה נחתוך &#40;מסומן באדום &#41;ונקבל שני חוטי חשמל &#44; חיובי ושלילי - לא משנה מה זה מה &#44; יש לחבר את החוטים אחד לעיגול הכחול הראשון והשני לעיגול השני &#44; מומלץ להלחים את החוטים ולסגור עם שרינק &#44; מי שאין ולא יודע יכול להשתמש בדבק חשמלאים - באיזולירבנד &#40;חשוב שלא יגיעו מים כי חשמל ומים זה מסוכן ובאחריותכם &#41;","כעת את השעון חשמל נחבר לשקע ונכוון ע&#39;פ הצורך &#44; באיזה ימים להשקות &#44;כמה זמן השקיה וכו .. את השנאי לחבר לשעון החשמל.","את צינור המים אני העברתי דרך הקיר &#44; כל אחד שיעשה לרוחו &#44; ופיצלתי אותו לכמה אזורי השקיה .","חיברתי טפטפות בכל המקומות ששתלתי דברים &#40; ישנם סוגי טפטפות שונות &#44; צריך להתאים עפ הצמח &#44;  הטפטפות השונות נותנות כמויות שונות של מים לשעה &#41; ","מקווה שעזרתי למישהו &#44; בהצלחה ... "]', '["images/guides/auto_watering/auto_watering_0.jpg","images/guides/auto_watering/auto_watering_1.jpg","images/guides/auto_watering/auto_watering_2.jpg","images/guides/auto_watering/auto_watering_3.jpg","images/guides/auto_watering/auto_watering_4.jpg","images/guides/auto_watering/auto_watering_5.jpg","images/guides/auto_watering/auto_watering_6.jpg","images/guides/auto_watering/auto_watering_7.jpg"]', '[""]', '', '["textarea","text_and_img","text_and_img","text_and_img","text_and_img","text_and_img","text_and_img","text_and_img"]');
INSERT INTO `guides` VALUES (167, 1, '6', '1', 'install_kodi_17_with_abeksis', 'התקנת קודי 17 למחשב פלוס התקנת אבקסיס', 'install kodi 17 with abeksis', 'התקנת קודי גירסא 17 פלוס אבקסיס - הסברים צעד אחר צעד, אבקסיס מתקין את כל מה שצריך , שפה כתוביות ערוצים live ועוד .. ', '', 0, '', '[[""]]', '["נפעיל את הקובץ שירד, בהודעה שתקפוץ לחצו yes","לחצו Next","לחצו I Agree","לחצו Next","לחצו install","תסמנו את הריבוע ב v ואז לחצו על finish","כשקודי יעלה תקפוץ הודעת אבטחה של וינדוס יש לאשר אותה Allow access","נבחר באיקון של הגלגל שיניים בצד שמאל למעלה","כעת נבחר ב File manager","נלחץ על Add source","בחלון שנפתח נלחץ על <None>","כעת נזין את הכתובת http://repo.abeksis.com ונלחץ על ok","נלחץ על הרובריקה ->המלבן למטה  ","כעת נזין את השם abeksis ונלחץ ok","נחזור למסך הראשי חזרה אחרוה ניתן על ידי Backspace במקלדת , ונבחר Add-ons","כעת נבחר בארגז בצד שמאל למעלה ","נלחץ על Install from zip file","בחלון שיקפוץ בנחר ב settings","כעת נבחר ב Unknown sources ","בחלון שיקפוץ נבחר ב Yes","וכעת נחזור אחורה עי כפתור  Backspace במקלדת ונלחץ שוב על Install from zip file ","בחלון שיפתח נבחר ב abeksis","וכעת נבחר ב repository.abeksis.zip","כעת נבחר ב Install from repository","נבחר Abeksis repsitory","כעת נבחר ב Program add-ons","כעת יש לבחור ב Abeksis wizard","ואז כמובן נלחץ על Install","נחזור ארבעה שלבים אחורה עד שנגיע למסך של Add-ons ושם נעמוד על Program add-ons","ואז נקליק על האופציה בצד ימין Abeksis wizard","בחלון שנפתח נקליק על Wizard for kodi 17 General","ברגע שתסתים ההתקנה יקפוץ חלון שיבקש לכבות את הקודי שההגדרות החדשות יכנסו לתוקף וכמובן נאשר זאת עי לחיצה על ok","נפעיל מחדש את kodi ומזל טוב קיבלתם קודי מוכן לפעולה ",""]', '["images/guides/install_kodi_17_with_abeksis/install_kodi_17_with_abeksis_0.jpg","images/guides/install_kodi_17_with_abeksis/install_kodi_17_with_abeksis_1.png","images/guides/install_kodi_17_with_abeksis/install_kodi_17_with_abeksis_2.png","images/guides/install_kodi_17_with_abeksis/install_kodi_17_with_abeksis_3.png","images/guides/install_kodi_17_with_abeksis/install